import * as React from "react";

interface ICount {
  count: number;
  //   State: number;
}

// interface IState {
//   State: number;
// }

class Counter extends React.Component<ICount> {
  State: ICount = {
    count: 0,
  };
  CountUp = () => {
    const { count } = this.props;
    // count: count + 1;
    count;
    console.log("\n\n\n up \n\n\n", count);
    this.setState({
      text: count,
    });
    // console.log("\n\n\n up \n\n\n", State);
  };
  CountDown = () => {
    // const { count } = this.props;
    // console.log("\n\n\n Down \n\n\n", count);
  };
  render() {
    const { count } = this.props;
    return (
      <div>
        <h1>프로필</h1>
        <button onClick={this.CountUp}>+</button>
        <div>{count}</div>
        <button onClick={this.CountDown}>-</button>
      </div>
    );
  }
}

export default Counter;
