import * as React from "react";

interface IText {
  text: string;
}

interface IProps {}

class Exfile extends React.Component<IProps, IText> {
  state: IText = {
    text: "",
  };
  ExFor = () => {
    for (var i = 0; i < 10; i++) {
      this.setState(({ text }) => ({ text: text }));
      //   this.setState(({ text }) => ({ text: text + i + "love" }));
      console.log("\n\n\n  i\n\n\n", i);
      console.log("\n\n\n  text\n\n\n", this.state.text);
    }
  };
  render() {
    return (
      <div>
        <h1>안녕</h1>
        <button onClick={this.ExFor}>시작</button>
        <div>{this.state.text}</div>
      </div>
    );
  }
}

export default Exfile;
