import * as React from 'react'

interface IText {
  text: string
}

interface IProps {}

class Exfile extends React.Component<IProps, IText> {
  state: IText = {
    text: '',
  }
  Ins = () => {
    if (this.state.text == '') {
      this.setState({ text: '추가' })
    } else {
      this.setState(({ text }) => ({ text: text + '|' + '추가' }))
    }
    console.log('\n\n\n  text\n\n\n', this.state.text)
    // }
  }

  Del = () => {
    const ex = this.state.text.split('|').pop()
    console.log('\n\n del \n\n\n', ex)
    this.setState({ ex: ex.pop() })
    // if (ex.length == 1) {
    // } else {
    //   this.setState({} => { ex : ex.pop(-1)})
    // }
  }
  render() {
    return (
      <div>
        <h1>안녕</h1>
        <button onClick={this.Ins}>추가</button>
        <button onClick={this.Del}>삭제</button>
        <div>{this.state.text}</div>
      </div>
    )
  }
}

export default Exfile
