import * as React from "react";

interface IText {
  text: string;
}

interface IProps {}

class Exfile extends React.Component<IProps, IText> {
  state: IText = {
    text: "",
  };
  install = () => {
    // for (var i = 0; i < 10; i++) {
    //   this.setState({ text: string(i) });
    //   this.setState(({ text }) => ({ text: i }));
    this.setState(({ text }) => ({ text: text + "LOVE" }));
    console.log("\n\n\n  text\n\n\n", this.state.text);
    // }
  };
  render() {
    return (
      <div>
        <h1>안녕</h1>
        <button onClick={this.install}>추가</button>
        <div>{this.state.text}</div>
      </div>
    );
  }
}

export default Exfile;
